using DevIO.Api.ViewModels;
using Microsoft.AspNetCore.Http;
using System;
using System.IO;
using System.Reflection;

namespace DevIO.IntegrationTests.Helpers
{
    public static class ProdutoViewModelTestsHelper
    {   
        public static ProdutoViewModel ObterInstancia(FornecedorViewModel fornecedorVM)
        {
            return new ProdutoViewModel
            {
                Id = Guid.NewGuid(),
                Ativo = true,
                DataCadastro = DateTime.Now,
                Descricao = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Curabitur turpis est, pulvinar a fermentum non, porttitor",
                FornecedorId = fornecedorVM.Id,
                Imagem = "teste_imagem.png",
                ImagemUpload = "",
                Nome = "Um produto de teste",
                NomeFornecedor = fornecedorVM.Nome,
                Valor = (decimal)new Random().NextDouble() * 10 + 100000,
            };
        }
        
        public static ProdutoViewModel ObterInstancia(FornecedorViewModel fornecedorVM, string directoryPath)
        {
            ProdutoViewModel produtoVM = ObterInstancia(fornecedorVM);

            // Escreve arquivo que será atualizado
            string pathFile = Path.Combine(directoryPath, produtoVM.Imagem);
            File.WriteAllBytes(pathFile, Convert.FromBase64String(produtoVM.ImagemUpload));

            return produtoVM;
        }
        
        public static (ProdutoImagemViewModel, Stream) ObterInstanciaParaImagemGrande(FornecedorViewModel fornecedorVM)
        {
            string directoryTempFile = Path.Combine(
                Path.GetDirectoryName(Assembly.GetEntryAssembly().Location.Substring(0, Assembly.GetEntryAssembly().Location.IndexOf("bin\\"))),
                "TempFiles"
            );

            string nomeImagem = "teste_imagem_grande.png";
            string pathTempFile = Path.Combine(directoryTempFile, nomeImagem);

            string base64Image = ObterInstancia(fornecedorVM).ImagemUpload;
            File.WriteAllBytes(pathTempFile, Convert.FromBase64String(base64Image));

            Stream stream = File.OpenRead(pathTempFile);
            var formFile = new FormFile(stream, 0, stream.Length, null, nomeImagem) 
            {
                Headers = new HeaderDictionary(),
                ContentType = "image/png"
            };

            return (new ProdutoImagemViewModel
            {
                Id = Guid.NewGuid(),
                Ativo = true,
                DataCadastro = DateTime.Now,
                Descricao = "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
                FornecedorId = fornecedorVM.Id,
                Imagem = nomeImagem,
                ImagemUpload = formFile,
                Nome = "Um produto de teste com imagem grande",
                NomeFornecedor = fornecedorVM.Nome,
                Valor = (decimal)new Random().NextDouble() * 10 + 100000,
            }, stream);
        }
        
        public static (ProdutoImagemViewModel, Stream) ObterInstanciaParaImagemGrande(FornecedorViewModel fornecedorVM, string directoryPath)
        {
            (ProdutoImagemViewModel produtoVM, Stream fileStream) = ObterInstanciaParaImagemGrande(fornecedorVM);

            // Escreve arquivo que será atualizado
            string pathFile = Path.Combine(directoryPath, produtoVM.Imagem);
            using var destination = new FileStream(pathFile, FileMode.Create);
            fileStream.CopyTo(destination);

            return (produtoVM, fileStream);
        }

        public static ProdutoViewModel ObterInstanciaPorTransformacao(ProdutoImagemViewModel produtoImagemVM)
        {
            return new ProdutoViewModel
            {
                Id = produtoImagemVM.Id,
                Ativo = produtoImagemVM.Ativo,
                DataCadastro = produtoImagemVM.DataCadastro,
                Descricao = produtoImagemVM.Descricao,
                FornecedorId = produtoImagemVM.FornecedorId,
                Imagem = produtoImagemVM.Imagem,
                ImagemUpload = string.Empty,
                Nome = produtoImagemVM.Nome,
                NomeFornecedor = produtoImagemVM.NomeFornecedor,
                Valor = produtoImagemVM.Valor,
            };
        }
    }
}
